from openpyxl import load_workbookclass ExcelHandler:    """封装Excel数据的类"""    def __init__(self, filename: str, sheet_name: str) -> None:        self.filename = filename        # 表格对象        self.workbook_obj = load_workbook(filename)        # 表单对象        self.sheet_obj = self.workbook_obj[sheet_name]        # 获取所有的行数据        self.cell_list = self.sheet_obj.iter_rows(values_only=True)        self.workbook_obj.close()    def case_data(self) -> list:        """        Returns: 封装后的测试数据列表        """        data_list = list(self.cell_list)        # 拼接好的数据列表        case_data_list = []        # 拼接数据        for i in data_list[1:]:            case_data_list.append(dict(zip(data_list[0], i)))        return case_data_list    def write_data(self, index: str, data: str) -> None:        """        写入数据到表格        """        self.sheet_obj[index] = data        self.workbook_obj.save(self.filename)if __name__ == '__main__':    exec_handler = ExcelHandler(r"../test_cases.xlsx", "post")    print(exec_handler.case_data())