import timeimport uuidimport hashlibclass HandlerSign(object):    app_key = "b11feb4b938f4d1e85d657a1b5a534a5"    def __init__(self, device_id: str) -> None:        self.device_id = device_id    def sign_rand(self) -> str:        """        :return: 返回组装sign        """        # 当前时间戳        now = str(int(time.time()))        # 随机字母数 uuid        rand_str = self.__random_Alphanumeric()        return self.__get_md5(            now + self.app_key + rand_str + self.device_id) + " " + now + " " + rand_str + " " + "v2"    def __random_Alphanumeric(self):        uid = str(uuid.uuid4())        return "".join(uid.split("-"))    def __get_md5(self, value: str) -> str:        return hashlib.md5(str(value).encode("utf-8")).hexdigest()if __name__ == '__main__':    print(HandlerSign("uCode_06377883b20734f2").sign_rand())